defmodule DoorCodeTest do
  use ExUnit.Case
  doctest DoorCode

  test "example 1", do: assert "1985" == example_1_input |> DoorCode.decode

  def example_1_input do
    "ULL
RRDDD
LURDL
UUUUD"
  end

  test "input" do
    assert "56983" = input |> DoorCode.decode
  end

  def input do
    "



"
  end
end
